  A.TASK_CODE
, A.TASK_TITLE
, A.TASK_START_DATE
, A.TASK_END_DATE
, A.TASK_PROGRESS
, A.TASK_BODY
, A.TASK_VERSION
, A.SPRINT_CODE
, A.PROJECT_CODE
, A.MEMBER_NO
)
VALUES
(
  SEQ_TASK_CODE.NEXTVAL
, #{ title }
, #{ startDate }
, #{ endDate }
, '진행전'
, #{ body }
, 1
, #{ sprintCode }
, #{ projectCode }
, #{ memberNo }
)
